def search_ext( all_files, ext ):
    result = []
    # Iterate over all given file names
    for file_name in all_files:
        # Test if the current file has the extension ext
        if file_name[-3:] == ext:
            # append the current file_name to the result list
            result.append(file_name)
    # return the result list
    return result

